INSERT INTO [dbo].[SeriesItems] ([SeriesId], [Title], [Description], [DistributorId], [Length], [FormatId], [ReleaseDate]) VALUES (49,'Visions','Kazuki is plagued by visions of giant robots that no one else can see. But all of the years of ridicule are about to pay off! Kazuki gets ensnared by a pretty face, and then a mad scientist''s experiment tosses him into this parallel world!  Now the danger is very real and his allies are twice as strange as he ever imagined!  On the plus side, he gets to drive the robots!','Pioneer',3,'')
