INSERT INTO SeriesItems (Title,FormatId,SeriesId) VALUES ('Martian Successor Nadesico : Mission to Mars',3,14);
INSERT INTO SeriesItems (Title,FormatId,SeriesId) VALUES ('Martian Successor Nadesico : Danger Zones',3,14);
